import React from 'react'
import {List, ListItem, ListItemText, FormControlLabel, Checkbox, Button} from '@material-ui/core';

var MyForm = React.createClass({
    save: function(){
        console.log(this.refs.check_me.value);
    },

    toggleCheckboxValue: () => {
        this.setState({checkBoxValue: !this.state.checkboxValue});
    }
});

const Einkaufen = (props) => {
    return(
        <React.Fragment>
            <p>Würdest du auch wenn nötig einkaufen gehen?</p>
            <div>
        <Checkbox
            value={this.state.checkboxValue} onChange={this.toggleCheckboxValue}
            value="primary"
            inputProps={{ 'aria-label': 'primary checkbox' }}
        />
            </div>
            <Button variant="contained" color="secondary" onClick={this.save}>>
        Absenden
      </Button>
        </React.Fragment>
        )
}
export default Einkaufen;

