import React from 'react'


const Schritte = (props) => {
    return(
        <h3> Schritte</h3>
        )
}
export default Schritte;

// Schritte die bei der Herstellung des jeweiligen Cocktails beachtet werden müssen.
zutaten.forEach(function(item, array) {
    switch (item) {
        case item = "Old Fashioned":
            return (
                <ol>
                    <li> Zerstoße in einem Becherglas den Zuckerwürfel. </li>
                    <li> Füge Würzbitter hinzu. </li>
                    <li> Danach mit Boubon füllen </li>
                    <li> Mit Orange garnieren </li>
                </ol>
                );
            break;
        case item = "Mojito":
            return (
                <ol>
                    <li> Zerstoße in einem Longdrinkglas Minzen. </li>
                    <li> Füge den Zuckersirup hinzu. </li>
                    <li> Danach das Glas mit Eiswürfeln füllen. </li>
                    <li> Limettensaft und leichten Rum hinzugeben. </li>
                    <li> Danach mit Sodawasser auffüllen. </li>
                    <li> Mit Minzwasser garnieren. </li>
                </ol>
            );
            break;
        case item = "Vodka Mojito":
            return (
                <ol>
                    <li> Zerstoße in einem Longdrinkglas Minze und Limette. </li>
                    <li> Füge Absolute Vodka und Zuckersirup hinzu. </li>
                    <li> Danach den Rest mit Sodawasser füllen. </li>
                </ol>
            );
            break;
        case item = "Skylab":
            return (
                <ol>
                    <li> Ein Weinglas mit Eiswürfeln füllen. </li>
                    <li> Alle Zutaten zugeben. </li>
                    <li> Danach mit einer Maraschino-Kirsche und einer Orange garnieren. </li>
                </ol>
            );
            break;
        case item = "So Sunrise":
            return (
                <ol>
                    <li> Ein Longdrinkglas mit Eiswürfeln füllen. </li>
                    <li> Alle Zutaten zugeben. </li>
                    <li> Danach mit der Orange garnieren. </li>
                </ol>
            );
            break;
        case item = "Tequila Sunrise":
            return (
                <ol>
                    <li> Ein Longdrinkglas mit Eiswürfeln füllen. </li>
                    <li> Tequila und Orangensaft zugeben. </li>
                    <li> Grenadine hinzugeben. </li>
                    <li> Danach mit einer Orange und einer Krische garnieren. </li>
                </ol>
            );
            break;
    }
})


